mapper.select:
select

from student_tb

	
		instr(username, #{keyWords}) > 0
	
	
		and status=#{status}
	
	
		and profession_id=#{professionId}

select

from student_tb
where account=#{account}

	and password=#{password}

mapper.delete:
delete from student_tb
where student_id in

	#{item}
